import './App.css';
import TaskForm from "./TaskForm";
import Task from "./Task";
import {useEffect, useState} from "react";

function App() {
  const [tasks,setTasks] = useState([]);

  useEffect(() => {
    if (tasks.length === 0) return;
    localStorage.setItem('tasks', JSON.stringify(tasks));
  }, [tasks]);

  useEffect(() => {
    const tasks = JSON.parse(localStorage.getItem('tasks'));
    setTasks(tasks || []);
  }, []);

  function agregarTask(nombre) {
    setTasks(prev => {
      return [...prev, {nombre:nombre,hecho:false}];
    });
  }

  function eliminarTask(indexEliminar) {
    setTasks(prev => {
      return prev.filter((taskObject,index) => index !== indexEliminar);
    });
  }

  function actualizarTaskHecho(taskIndex, nuevoHecho) {
    setTasks(prev => {
      const nuevosTasks = [...prev];
      nuevosTasks[taskIndex].done = nuevoHecho;
      return nuevosTasks;
    });
  }

}

export default App;
