import './App.css';
import TaskForm from "./TaskForm";
import Task from "./Task";
import {useEffect, useState} from "react";

function App() {
  const [tasks,setTasks] = useState([]);

  useEffect(() => {
    if (tasks.length === 0) return;
    localStorage.setItem('tasks', JSON.stringify(tasks));
  }, [tasks]);

  useEffect(() => {
    const tasks = JSON.parse(localStorage.getItem('tasks'));
    setTasks(tasks || []);
  }, []);

  function agregarTask(name) {
    setTasks(prev => {
      return [...prev, {name:name,done:false}];
    });
  }

  function eliminarTask(indexToEliminar) {
    setTasks(prev => {
      return prev.filter((taskObject,index) => index !== indexToEliminar);
    });
  }

  function actualizarTaskDone(taskIndex, newDone) {
    setTasks(prev => {
      const nuevosTasks = [...prev];
      nuevosTasks[taskIndex].done = newDone;
      return nuevosTasks;
    });
  }

  const numberComplete = tasks.filter(t => t.done).length;
  const numberTotal = tasks.length;

  function getMessage() {
    const percentage = numberComplete/numberTotal * 100;
    if (percentage === 0) {
      return 'Try to do at least one! 🙏';
    }
    if (percentage === 100) {
      return 'Nice job for today! 🏝';
    }
    return 'Keep it going 💪🏻';
  }

  function renameTask(index,newName) {
    setTasks(prev => {
      const newTasks = [...prev];
      newTasks[index].name = newName;
      return newTasks;
    })
  }


}
